// RentOutBikeForm.js
import React, { useState } from 'react';
import './RentOutBikeForm.css';

const RentOutBikeForm = ({ handleRentOut }) => {
  const [bikeInfo, setBikeInfo] = useState({
    vehicleName: '',
    milesDriven: '',
    fuelStatus: '',
    description: '',
    image: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBikeInfo((prevInfo) => ({
      ...prevInfo,
      [name]: value,
    }));
  };

  const handleImageChange = (e) => {
    const imageFile = e.target.files[0];
    setBikeInfo((prevInfo) => ({
      ...prevInfo,
      image: imageFile,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Call the parent component's function to handle the submission
    handleRentOut(bikeInfo);
  };

  return (
    <form className="rent-out-form" onSubmit={handleSubmit}>
      <div className="form-container">
        <label htmlFor="vehicleName">Vehicle Name:</label>
        <input
          type="text"
          id="vehicleName"
          name="vehicleName"
          value={bikeInfo.vehicleName}
          onChange={handleChange}
          required
        />

        <label htmlFor="milesDriven">Miles Driven:</label>
        <input
          type="text"
          id="milesDriven"
          name="milesDriven"
          value={bikeInfo.milesDriven}
          onChange={handleChange}
          required
        />

        <label htmlFor="fuelStatus">Fuel Status:</label>
        <select
          id="fuelStatus"
          name="fuelStatus"
          value={bikeInfo.fuelStatus}
          onChange={handleChange}
          required
        >
          <option value="">Select Fuel Status</option>
          <option value="full">Full</option>
          <option value="partial">Partial</option>
          <option value="empty">Empty</option>
        </select>

        <label htmlFor="description">Description:</label>
        <textarea
          id="description"
          name="description"
          value={bikeInfo.description}
          onChange={handleChange}
          required
        />

        <label htmlFor="image">Upload Image:</label>
        <input
          type="file"
          id="image"
          name="image"
          accept="image/*"
          onChange={handleImageChange}
          required
        />

        <button type="submit">Post Ad</button>
      </div>
    </form>
  );
};

export default RentOutBikeForm;
