import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Slider from 'react-slick'; // Import the carousel component
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const RentBike = ({ navigateTo }) => {
  const [bikes, setBikes] = useState([]);
  const [selectedBike, setSelectedBike] = useState(null);
  const [isBookingSuccessful, setBookingSuccessful] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const fetchBikes = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5000/bikes');
        setBikes(response.data);
      } catch (error) {
        console.error('Error fetching bikes:', error);
      }
    };
    fetchBikes();
  }, []);

  const handleRentNow = async () => {
    if (!selectedBike) {
      alert('Please select a bike to rent.');
      return;
    }
    setIsLoading(true);

    // Here, implement the booking logic to interact with your backend
    try {
      const response = await axios.post('http://127.0.0.1:5000/book-bike', {
        bikeId: selectedBike
      });
      if (response.status === 200) {
        setBookingSuccessful(true);
      } else {
        alert('Booking failed. Please try again later.');
      }
    } catch (error) {
      console.error('Error booking the bike:', error);
      alert('Booking failed. Please try again later.');
    }

    setIsLoading(false);
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  return (
    <div className="rent-bike">
      <h2>Rent a Bike</h2>
      <p>Explore the campus with ease by renting a bike.</p>
      <Slider {...settings}>
        {bikes.map(bike => (
          <div key={bike.id} className="bike-slide">
            <img src={bike.imageUrl} alt={bike.vehicleModel} />
            <h3>{bike.vehicleModel}</h3>
            <p>{bike.vehicleDescription}</p>
            <button onClick={() => setSelectedBike(bike.id)}>Select This Bike</button>
          </div>
        ))}
      </Slider>
      <button onClick={handleRentNow} disabled={isLoading}>Rent Now</button>
      <button onClick={() => navigateTo('landing')}>Go Back</button>

      {isBookingSuccessful && (
        <div className="success-message">
          Booking successful! You will receive a confirmation shortly.
        </div>
      )}
    </div>
  );
};

export default RentBike;
