import React, { useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from './AuthContext.js'; // Import AuthContext to access the token
import './RentOutBike.css';

const RentOutBike = ({ navigateTo }) => {
  const [isListingCreated, setListingCreated] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    milesDriven: '',
    hasFuel: false,
  });

  const { token } = useContext(AuthContext); // Retrieve the token from AuthContext

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleRentOut = async () => {
    try {
      const response = await axios.post(
        'http://127.0.0.1:5000/rent-out',
        {
          ...formData,
          chargePerHour: formData.chargePerHour, // Assuming you have a chargePerHour field in your form
          // Add other fields as necessary
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (response.status === 201) {
        setListingCreated(true);
      } else {
        alert('Failed to create listing. Please try again later.');
      }
    } catch (error) {
      console.error('Error creating bike listing:', error);
      alert('Failed to create listing. Please try again later.');
    }
  };

  return (
    <div className="rent">
      <div className="flex-container1_rob">
        {/* Content inside the container */}
        <div className="box-content0_rob">
          <span className="typing-animation1_rob">Rent it Out!</span>
          <div className="typing-animation1_rob">
            Make money by renting out your bike when you're not using it.
          </div>
        </div>
      </div>
      <div className='frm'>
        <h2 className="form-title">Post Your Ad</h2>
        <div className="toRent">
          <form className="horizontal-form">
            <div className="form-half">
              <label>
                Vehicle Name:
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  placeholder="Type Vehicle Name"
                />
              </label>
              <label>
                Vehicle Description:
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Miles Driven:
                <input
                  type="text"
                  name="milesDriven"
                  value={formData.milesDriven}
                  onChange={handleInputChange}
                />
              </label>
              <label className="checkbox-label">
                Has Fuel:
                <input
                  type="checkbox"
                  name="hasFuel"
                  checked={formData.hasFuel}
                  onChange={handleInputChange}
                />
              </label>
              {/* Placeholder for file upload */}
              <label>
                Pictures:
                <input
                  type="file"
                  name="pictures"
                  multiple
                  onChange={handleInputChange}
                />
              </label>
            </div>
          </form>
        </div>
        <div className="button-container">
          <button onClick={handleRentOut} className="rent-out-button">Rent Out</button>
          <button onClick={() => navigateTo('landing')} className="go-back-button">Go Back</button>
        </div>
        {isListingCreated && (
          <div className="success-message">
            Your bike listing has been created successfully.
          </div>
        )}
      </div>
    </div>
  );
};

export default RentOutBike;
