import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext'; // Import useAuth

const ListMotorbike = () => {
    const [motorbike, setMotorbike] = useState({
        model: '',
        price: '',
        description: ''
    });
    const { isLoggedIn } = useAuth(); // Use Auth context

    const handleInputChange = (e) => {
        setMotorbike({ ...motorbike, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!isLoggedIn) {
            alert('You must be logged in to list a motorbike.');
            return;
        }

        try {
            const token = 'YOUR_JWT_TOKEN'; // Replace with actual token from context or local storage
            const response = await axios.post('http://localhost:5000/rent-out', motorbike, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            alert('Motorbike listed successfully');
        } catch (error) {
            console.error('Error listing motorbike:', error);
            alert('Failed to list motorbike');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>List Your Motorbike for Rent</h2>
            <input
                type="text"
                name="model"
                value={motorbike.model}
                onChange={handleInputChange}
                placeholder="Model"
                required
            />
            <input
                type="text"
                name="price"
                value={motorbike.price}
                onChange={handleInputChange}
                placeholder="Price per Day"
                required
            />
            <textarea
                name="description"
                value={motorbike.description}
                onChange={handleInputChange}
                placeholder="Description"
                required
            />
            <button type="submit">List Motorbike</button>
        </form>
    );
};

export default ListMotorbike;
