// RentOutBike.js
import React, { useState } from 'react';
import './RentOutBike.css';

const RentOutBike = ({ navigateTo }) => {
  const [listingData, setListingData] = useState({
    vehicleName: '',
    milesDriven: '',
    hasFuel: true,
    images: [],
    description: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setListingData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleCheckboxChange = () => {
    setListingData((prevData) => ({
      ...prevData,
      hasFuel: !prevData.hasFuel,
    }));
  };

  const handleImageUpload = (e) => {
    const files = e.target.files;
    const imagesArray = Array.from(files).map((file) => URL.createObjectURL(file));

    setListingData((prevData) => ({
      ...prevData,
      images: [...prevData.images, ...imagesArray],
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Implement your logic to submit the form data
    console.log('Form submitted:', listingData);
    // Reset the form or navigate to another page as needed
  };

  return (
    <div className="rent">
      <div className="flex-container1_rob">
        {/* Dark overlay */}

        {/* Content inside the container with the form */}
        <div className="box-content0_rob">
          <h1>Rent Out Your Bike</h1>
          <form onSubmit={handleSubmit}>
            <label htmlFor="vehicleName">Vehicle Name:</label>
            <input
              type="text"
              id="vehicleName"
              name="vehicleName"
              value={listingData.vehicleName}
              onChange={handleInputChange}
              required
            />

            <label htmlFor="milesDriven">Miles Driven:</label>
            <input
              type="text"
              id="milesDriven"
              name="milesDriven"
              value={listingData.milesDriven}
              onChange={handleInputChange}
              required
            />

            <label>
              <input
                type="checkbox"
                checked={listingData.hasFuel}
                onChange={handleCheckboxChange}
              />
              Has Fuel
            </label>

            <label htmlFor="images">Upload Images:</label>
            <input
              type="file"
              id="images"
              name="images"
              accept="image/*"
              multiple
              onChange={handleImageUpload}
            />

            <label htmlFor="description">Vehicle Description:</label>
            <textarea
              id="description"
              name="description"
              value={listingData.description}
              onChange={handleInputChange}
              required
            ></textarea>

            <button type="submit">Submit Ad</button>
          </form>
        </div>
      </div>

      <div>
        <button onClick={() => navigateTo('landing')}>Go Back</button>
      </div>
    </div>
  );
};

export default RentOutBike;
