import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext'; // Ensure this path is correct
import './RentOutBike.css';

const RentOutBike = ({ navigateTo }) => {
  const [isListingCreated, setListingCreated] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    milesDriven: '',
    hasFuel: false,
  });

  const { isLoggedIn, token } = useAuth(); // Retrieve isLoggedIn status and JWT token from AuthContext

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleRentOut = async () => {
    if (!isLoggedIn) {
      alert('You must be logged in to rent out a bike');
      return;
    }

    try {
      const payload = {
        ...formData,
        milesDriven: parseInt(formData.milesDriven, 10) || 0,
        hasFuel: formData.hasFuel ? 'Yes' : 'No'
      };

      const response = await axios.post(
        'http://127.0.0.1:5000/rent-out',
        payload,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (response.status === 201) {
        setListingCreated(true);
      } else {
        alert('Failed to create listing. Please try again later.');
      }
    } catch (error) {
      console.error('Error renting out the bike:', error);
      alert('Failed to create listing. Please try again later.');
    }
  };

  return (
    <div className="rent-out-bike">
      <h2>Rent Out Your Bike</h2>
      <form className="bike-form">
        <label>
          Vehicle Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            placeholder="Enter bike name"
          />
        </label>
        <label>
          Vehicle Description:
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            placeholder="Describe your bike"
          />
        </label>
        <label>
          Miles Driven:
          <input
            type="number"
            name="milesDriven"
            value={formData.milesDriven}
            onChange={handleInputChange}
            placeholder="Mileage"
          />
        </label>
        <label>
          Has Fuel:
          <input
            type="checkbox"
            name="hasFuel"
            checked={formData.hasFuel}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Pictures (Placeholder):
          <input
            type="file"
            name="pictures"
            multiple
            disabled // Disabled as it's a placeholder
          />
        </label>
        <button type="button" onClick={handleRentOut}>Submit Listing</button>
      </form>
      {isListingCreated && <div className="success-message">Listing created successfully!</div>}
      <button onClick={() => navigateTo('landing')}>Go Back</button>
    </div>
  );
};

export default RentOutBike;
