// src/components/RentBike.js
import React, { useState } from 'react';
import './RentBike.css'
import axios from 'axios';


const RentBike = ({ navigateTo }) => {
  const [isBookingSuccessful, setBookingSuccessful] = useState(false);

  const handleRentNow = async (selectedBikeId) => {
    try {
        const token = localStorage.getItem('token'); // Get the stored JWT token
        const response = await axios.post('http://127.0.0.1:5000/book-bike', 
            { 
                bikeId: selectedBikeId 
            },
            {
                headers: {
                    Authorization: `Bearer ${token}` // Include the JWT token in the request header
                }
            }
        );
        if (response.status === 200) {
            setBookingSuccessful(true);
        } else {
            alert('Booking failed. Please try again later.');
        }
    } catch (error) {
        console.error('Error booking the bike:', error);
        alert('Booking failed. Please try again later.');
    }
};


  return (
    <div className="rent-bike">
            <div className="flex-container1_rent">
        {/* Dark overlay */}

        {/* Content inside the container with typing animation */}
        <div className="box-content0_rent">
          <span className="typing-animation1_rent">Want A Ride? </span>
          <br />
          <div className="typing-animation1_rent">
            We've Got You Covered..
          </div>
        </div>
      </div>

      <div className="card-container">
    {/* Card 1 */}
    <div className="card">
      <div className="card-image"></div>
      <div className="card-details">
        <h3>Vehicle Name</h3>
        <p>Owner: John Doe</p>
        <p>Charge per Hour: $10</p>
        <p>Availability: Yes</p>
      </div>
    </div>

    {/* Add more cards as needed */}

    {/* Example of a second row */}
    <div className="card">
      <div className="card-image"></div>
      <div className="card-details">
        <h3>Another Vehicle</h3>
        <h3>Owner: Jane Doe</h3>
        <p>Charge per Hour: $15</p>
        <p>Availability: No</p>
      </div>
    </div>

    <div className="card">
      <div className="card-image"></div>
      <div className="card-details">
        <h3>Another Vehicle</h3>
        <h3>Owner: Jane Doe</h3>
        <p>Charge per Hour: $15</p>
        <p>Availability: No</p>
      </div>
    </div>
    {/* Add more cards as needed */}
  </div>

      <button onClick={handleRentNow}>Rent Now</button>
      <button onClick={() => navigateTo('landing')}>Go Back</button>

      {/* Display a success message */}
      {isBookingSuccessful && (
        <div className="success-message">
          Booking successful! You will receive a confirmation shortly.
        </div>
      )}
    </div>
  );
};

export default RentBike;
